sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	// Use a position indicator to know which record to update
	$scope.notePos = 0;

	c.selectItem = function(idx) {  
		// Set the position indicator to the index value passed in 
		// from ng-click when a note is selected
		$scope.notePos = idx;
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$broadcast('selectNote', id);
	}
	// Subscribe to event.  Set the new title and note values on the 
	// c.data object
	$rootScope.$on('updateTitle', function(event,title,note) { 
		c.data.notes[$scope.notePos].title = title;
		c.data.notes[$scope.notePos].note = note;
	});

	c.newNote = function() {
		c.server.get({
			action: 'newNote'
		}).then(function(r) {
			c.data.notes.unshift(r.data.newNote);
			c.data.noteID = r.data.noteID;
			$rootScope.noteID = c.data.noteID;
			$rootScope.$broadcast('selectNote', c.data.noteID);
		});
	}

	$rootScope.$on('deleteNote', function(event,data) {
		c.data.notes.splice($scope.notePos, 1);
	});

}

sp_widget.script:
(function() {

	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};

		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');

		noteObj.note = noteGR.getValue('note').slice(0,20);

		data.notes.push(noteObj);
	}

	if (input) {
		if (input.action == 'newNote') {
			var newNote = new GlideRecord('x_snc_createnotes_note');
			newNote.newRecord();
			var id = newNote.insert();
			data.noteID = id;
			data.newNote = {
				'title': newNote.getValue('title'),
				'note': newNote.getValue('note'),
				'sys_id': newNote.getValue('sys_id')
			};
		}
	}
})();
